  FLIConfiguration::~FLIConfiguration()
  {
  }

  void FLIConfiguration::resetDefaultSettings()
  {
    conversionType = 0;
    yMin = -0.02f;
    yMax = 1.03f;
    scaleX = 1.0f;
    scaleY = 1.0f;
    offsetX = 0.0f;
    offsetY = 0.0f;
    saturationMult = 1.0f;
    saturationPow = 0.9f;
    gammaCorrection = 1.0f;
    monitorGamma = 2.2f;
    ditherMode = 1;
    ditherLimit = 0.25f;
    ditherDiffusion = 0.95f;
    enablePAL = true;
    xShift0 = -1;
    xShift1 = -1;
    borderColor = 0x00;
    verticalSize = 464;
    luminance1BitMode = false;
    disableInterpolation = false;
    noLuminanceInterlace = false;
    disableFLIEffects = false;
    colorInterlaceMode = 1;
    luminanceSearchMode = 2;
    luminanceSearchModeParam = 4.0f;
    mcColorErrorScale = 0.5f;
    multiColorQuality = 6;
    c64Color0 = 0x00;
    c64Color1 = 0x71;
    c64Color2 = 0x22;
    c64Color3 = 0x53;
    c64Color4 = 0x34;
    c64Color5 = 0x4F;
    c64Color6 = 0x1E;
    c64Color7 = 0x67;
    c64Color8 = 0x38;
    c64Color9 = 0x19;
    c64Color10 = 0x42;
    c64Color11 = 0x21;
    c64Color12 = 0x41;
    c64Color13 = 0x6F;
    c64Color14 = 0x4E;
    c64Color15 = 0x51;
    prgCompressionLevel = 0;
    outputFileFormat = 0;
    configChangeFlag = true;
  }

